body.type:
{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "description": "Check Case Schema",
  "type": "object",
  "additionalProperties": false,
  "properties": {
    "valuationId": {
      "description": "Unique valuation reference number",
      "$ref": "#/definitions/iddef"
    },
    "propertyLinkSubmissionId": {
      "description": "Unique reference number for the property link",
      "type": "string",
      "minLength": 1
    },
    "reasonCode": {
      "description": "Reason for Check case. Uses a lookup code.",
      "lookup": "codeOfGroundsDfe",
      "type": "string",
      "minLength": 1
    },
    "attachments": {
      "description": "File attachments supporting the case",
      "type": "array",
      "minItems": 0,
      "maxItems": 10,
      "items": {
        "$ref": "#/definitions/attachments"
      }
    },
    "declaration": {
      "description": "Declaration that the information given in this form is correct and complete.",
      "type": "boolean",
      "enum": [
        true
      ]
    },
    "smallerProposer": {
      "description": "Are you a smaller proposer (you are a smaller proposer if you have employed fewer than 10 people on average during the last 12 months. In that same period, you must also have had a turnover or balance sheet total of no more than £2 million.",
      "type": "boolean"
    },
    "experiencingHardship": {
      "description": "Are you experiencing hardship",
      "type": "boolean"
    },
    "reliefsReceived": {
      "description": "Forms of relief you are currently receiving",
      "type": "array",
      "items": {
        "type": "string",
        "enum": [
          "SMALL_BUSINESS_RATE_RELIEF",
          "RURAL_RATE_RELIEF",
          "BUS_RATE_HOL_CHILD_DAY_NURSERY",
          "EXPANDED_RETAIL_DISCOUNT",
          "RETAIL_HOSPITALITY_LEISURE_GRANT",
          "COVID-19_BUSINESS_SUPPORT_GRANT",
          "PAYMENTS_DEFERRED"
        ]
      }
    },
    "originalValuation": {
      "$ref": "#/definitions/detailedvaluation"
    },
    "proposedCharacteristics": {
      "description": "Proposed Characteristics is required when Reason Code is: confirm/change property details, court decision, or change in local area. Proposed Characteristics is not allowed when Reason Code is: split, merge, or remove property.",
      "$ref": "#/definitions/proposedCharacteristics"
    }
  },
  "title": "Check case submission request",
  "required": [
    "valuationId",
    "propertyLinkSubmissionId",
    "reasonCode",
    "declaration",
    "smallerProposer",
    "originalValuation"
  ],
  "definitions": {
    "adjustment": {
      "description": "Adjustments",
      "type": "object",
      "additionalProperties": false,
      "properties": {
        "adjustment": {
          "$ref": "#/definitions/referencedata",
          "description": "The type of adjustment. Uses a lookup code.",
          "lookup": "adjCode"
        },
        "polarity": {
          "description": "Polarity of adjustment",
          "type": "string",
          "minLength": 1,
          "enum": [
            "MINUS",
            "ZERO",
            "PLUS"
          ]
        }
      },
      "title": "Adjustment",
      "required": [
        "adjustment",
        "polarity"
      ]
    },
    "assessmentaddress": {
      "description": "Address",
      "type": "object",
      "additionalProperties": false,
      "properties": {
        "name": {
          "description": "Property name",
          "type": "string",
          "maxLength": 72,
          "minLength": 1
        },
        "firm": {
          "description": "Firm name",
          "type": "string",
          "maxLength": 50,
          "minLength": 1
        },
        "number": {
          "description": "Property number",
          "type": "string",
          "maxLength": 4,
          "minLength": 1
        },
        "street": {
          "description": "Street",
          "type": "string",
          "maxLength": 36,
          "minLength": 1
        },
        "subStreet1": {
          "description": "Sub street 1",
          "type": "string",
          "maxLength": 36,
          "minLength": 1
        },
        "subStreet2": {
          "description": "Sub street 2",
          "type": "string",
          "maxLength": 36,
          "minLength": 1
        },
        "subStreet3": {
          "description": "Sub street 3",
          "type": "string",
          "maxLength": 36,
          "minLength": 1
        },
        "postalDistrict": {
          "description": "Postal district",
          "type": "string",
          "maxLength": 36,
          "minLength": 1
        },
        "town": {
          "description": "Town",
          "type": "string",
          "maxLength": 36,
          "minLength": 1
        },
        "county": {
          "description": "County",
          "type": "string",
          "maxLength": 36,
          "minLength": 1
        },
        "postcode": {
          "description": "Postcode",
          "type": "string",
          "maxLength": 8,
          "minLength": 1
        }
      },
      "title": "Assessment address"
    },
    "attachments": {
      "description": "Attachments",
      "type": "object",
      "additionalProperties": false,
      "properties": {
        "filename": {
          "description": "File name",
          "type": "string",
          "minLength": 1
        }
      },
      "title": "Attachments",
      "required": [
        "filename"
      ]
    },
    "banding": {
      "description": "Banding information",
      "type": "object",
      "additionalProperties": false,
      "properties": {
        "accommodationBanding": {
          "$ref": "#/definitions/bandingdef"
        },
        "foodBanding": {
          "$ref": "#/definitions/bandingdef"
        },
        "liquorBanding": {
          "$ref": "#/definitions/bandingdef"
        },
        "region": {
          "description": "Region",
          "type": "string",
          "minLength": 1
        }
      },
      "title": "Banding",
      "required": [
        "region"
      ]
    },
    "bandingdef": {
      "description": "Bandings",
      "type": "object",
      "additionalProperties": false,
      "properties": {
        "category": {
          "description": "Category of banding (Food, liquor etc.)",
          "type": "string",
          "minLength": 1
        }
      },
      "title": "Banding def",
      "required": [
        "category"
      ]
    },
    "basis": {
      "description": "Basis of valuation",
      "type": "object",
      "additionalProperties": false,
      "properties": {
        "basisFairMaintainableTurnover": {
          "$ref": "#/definitions/basisfairmaintainableturnover"
        }
      },
      "title": "Basis",
      "required": [
        "basisFairMaintainableTurnover"
      ]
    },
    "basisfairmaintainableturnover": {
      "description": "Basis of valuation - fair maintainable turnover",
      "type": "object",
      "additionalProperties": false,
      "properties": {
        "adoptedFmrTotal": {
          "description": "Fair maintainable turnover",
          "maximum": 9223372036854775807,
          "type": "integer"
        },
        "adoptedTotalPct": {
          "description": "The percentage of the fair maintainable turnover used for the rateable value",
          "type": "number"
        },
        "banding": {
          "$ref": "#/definitions/banding"
        },
        "calculatedRv": {
          "description": "Receipts rateable value",
          "maximum": 9223372036854775807,
          "type": "integer"
        },
        "receipts": {
          "$ref": "#/definitions/receipts"
        }
      },
      "title": "Basis fair maintainable turnover"
    },
    "booleanDated": {
      "type": "object",
      "properties": {
        "value": {
          "type": "boolean"
        },
        "effectiveDate": {
          "type": "string",
          "format": "date"
        }
      },
      "required": [
        "value"
      ]
    },
    "bulkchanges": {
      "description": "Changes made to Survey information (Bulk)",
      "type": "object",
      "additionalProperties": false,
      "properties": {
        "basisOfMeasurementCode": {
          "description": "Basis of measurement. Uses a lookup code.",
          "lookup": "basisOfMeasurement",
          "type": "string",
          "minLength": 3,
          "maxLength": 3
        },
        "surveyUnit": {
          "$ref": "#/definitions/surveyunitchangeslist"
        },
        "parking": {
          "$ref": "#/definitions/parkingchanges"
        },
        "otherAdditions": {
          "description": "Other land or features",
          "type": "array",
          "items": {
            "$ref": "#/definitions/otheradditionschanges"
          }
        }
      },
      "title": "Bulk changes",
      "required": [
        "basisOfMeasurementCode",
        "surveyUnit",
        "parking",
        "otherAdditions"
      ]
    },
    "bulkvaluation": {
      "description": "Valuation and survey information for a Bulk property",
      "type": "object",
      "additionalProperties": false,
      "properties": {
        "bulkValuationDetails": {
          "$ref": "#/definitions/bulkvaluationdetails"
        }
      },
      "title": "Bulk valuation",
      "required": [
        "bulkValuationDetails"
      ]
    },
    "bulkvaluationdetails": {
      "description": "Valuation and survey details for a Bulk property",
      "type": "object",
      "additionalProperties": false,
      "properties": {
        "valuationSchemeReference": {
          "description": "Valuation scheme reference",
          "$ref": "#/definitions/iddef"
        },
        "basisOfMeasurement": {
          "$ref": "#/definitions/referencedata",
          "description": "The basis of measurement. Uses a lookup code.",
          "lookup": "basisOfMeasurement"
        },
        "surveyId": {
          "description": "Unique identifier for the Survey Unit",
          "$ref": "#/definitions/iddef"
        },
        "surveyUnit": {
          "$ref": "#/definitions/surveyunits"
        },
        "otherAdditions": {
          "$ref": "#/definitions/otheradditions"
        },
        "parking": {
          "$ref": "#/definitions/parking"
        },
        "plantAndMachinery": {
          "$ref": "#/definitions/plantandmachinery"
        },
        "totalBeforeEndAdjustments": {
          "description": "Total rateable value before any final adjustments",
          "type": "number"
        },
        "endAdjustment": {
          "$ref": "#/definitions/endadjustments"
        },
        "total": {
          "description": "Total rateable value before rounding applied",
          "type": "number"
        },
        "composite": {
          "description": "Part domestic (composite) property",
          "type": "boolean"
        }
      },
      "title": "Bulk valuation details",
      "required": [
        "valuationSchemeReference",
        "basisOfMeasurement",
        "surveyId",
        "surveyUnit",
        "totalBeforeEndAdjustments",
        "total",
        "composite"
      ]
    },
    "proposedCharacteristics": {
      "oneOf": [
        {
          "$ref": "#/definitions/bulkchanges"
        },
        {
          "$ref": "#/definitions/licensedpremiseschanges"
        }
      ]
    },
    "coverage": {
      "description": "Extent of coverage",
      "type": "string",
      "enum": [
        "Full",
        "Partial",
        "None"
      ]
    },
    "coverageDated": {
      "type": "object",
      "properties": {
        "value": {
          "type": "string",
          "enum": [
            "Full",
            "Partial",
            "None"
          ]
        },
        "effectiveDate": {
          "type": "string",
          "format": "date"
        }
      },
      "required": [
        "value"
      ]
    },
    "detailedvaluation": {
      "description": "Detailed valuation",
      "type": "object",
      "additionalProperties": false,
      "properties": {
        "valuationId": {
          "description": "Unique valuation Id",
          "$ref": "#/definitions/iddef"
        },
        "valuationType": {
          "$ref": "#/definitions/valuationtype"
        },
        "uarn": {
          "description": "Unique address reference number",
          "$ref": "#/definitions/iddef"
        },
        "address": {
          "$ref": "#/definitions/assessmentaddress"
        },
        "listYear": {
          "description": "List Year relating to the assessment",
          "type": "string",
          "minLength": 1
        },
        "effectiveDate": {
          "description": "Effective date of the assessment",
          "type": "string",
          "format": "date"
        },
        "rateableValue": {
          "description": "Rateable value",
          "type": "number"
        },
        "compositeProperty": {
          "description": "Part domestic (composite) property",
          "type": "boolean"
        },
        "valuationCharacteristics": {
          "$ref": "#/definitions/valuationdetails"
        }
      },
      "title": "Detailed valuation ",
      "required": [
        "valuationId",
        "valuationType",
        "uarn",
        "address",
        "listYear",
        "effectiveDate",
        "rateableValue",
        "compositeProperty",
        "valuationCharacteristics"
      ]
    },
    "endadjustment": {
      "description": "Adjustments to the whole",
      "type": "object",
      "additionalProperties": false,
      "properties": {
        "adjustment": {
          "$ref": "#/definitions/referencedata",
          "description": "The type of adjustment. Uses a lookup code.",
          "lookup": "adjCode"
        },
        "polarity": {
          "description": "Polarity of adjustment",
          "type": "string",
          "enum": [
            "MINUS",
            "ZERO",
            "PLUS"
          ],
          "minLength": 1
        },
        "adjPercent": {
          "description": "Percentage of the Adjustment",
          "type": "number"
        }
      },
      "title": "End adjustment",
      "required": [
        "adjustment",
        "polarity",
        "adjPercent"
      ]
    },
    "endadjustments": {
      "description": "Adjustments to the whole",
      "type": "object",
      "additionalProperties": false,
      "properties": {
        "endAdjustmentsList": {
          "description": "List of adjustments to the whole",
          "type": "array",
          "items": {
            "$ref": "#/definitions/endadjustment"
          }
        },
        "value": {
          "description": "Value of the adjustment",
          "type": "number"
        }
      },
      "title": "End adjustments",
      "required": [
        "endAdjustmentsList",
        "value"
      ]
    },
    "factorywarehousechanges": {
      "description": "Changes made to a factory or warehouse survey information",
      "type": "object",
      "additionalProperties": false,
      "properties": {
        "surveyUnitId": {
          "$ref": "#/definitions/surveyunitiddef"
        },
        "surveyUnitType": {
          "description": "Type of Survey unit - factory or warehouse",
          "type": "string",
          "enum": [
            "FACTORY",
            "WAREHOUSE"
          ]
        },
        "surveyLinesList": {
          "description": "List of floor areas",
          "type": "array",
          "minItems": 1,
          "items": {
            "$ref": "#/definitions/surveylines"
          }
        },
        "eavesHeight": {
          "description": "Eaves height",
          "type": "number",
          "maximum": 9999999.99,
          "minimum": 0
        },
        "numberOfStoreys": {
          "description": "Number of storeys, this should be the number of floors in the whole property",
          "type": "integer",
          "minimum": 0,
          "maximum": 999
        },
        "floorRangeStartCode": {
          "description": "Lowest floor of your property (this should be the lowest floor of your part of the property, not the lowest floor of the whole property). Uses a lookup code.",
          "lookup": "floor",
          "type": "string",
          "minLength": 1
        },
        "floorRangeEndCode": {
          "description": "Highest floor of your property (this should be the highest floor of your part of the property, not the highest floor of the whole property). Uses a lookup code.",
          "lookup": "floor",
          "type": "string",
          "minLength": 1
        },
        "yearBuiltCode": {
          "description": "Year built. Uses a lookup code.",
          "lookup": "year",
          "type": "string",
          "maxLength": 2,
          "minLength": 1
        },
        "yearRefurbishedCode": {
          "description": "Year refurbished. Uses a lookup code.",
          "lookup": "year",
          "type": "string",
          "maxLength": 2,
          "minLength": 1
        },
        "centralHeating": {
          "$ref": "#/definitions/coverageDated"
        },
        "airConditioning": {
          "$ref": "#/definitions/coverageDated"
        },
        "sprinklers": {
          "$ref": "#/definitions/sprinklersDated"
        },
        "passengerLift": {
          "description": "Passenger lift",
          "$ref": "#/definitions/booleanDated"
        },
        "passengerLiftAllFloors": {
          "description": "Passenger lift - all floors",
          "$ref": "#/definitions/booleanDated"
        },
        "goodsLift": {
          "description": "Goods lift",
          "$ref": "#/definitions/booleanDated"
        },
        "goodsLiftAllFloors": {
          "description": "Goods lift - all floors",
          "$ref": "#/definitions/booleanDated"
        }
      },
      "title": "Factory warehouse changes",
      "required": [
        "surveyUnitId",
        "surveyUnitType",
        "surveyLinesList",
        "eavesHeight",
        "numberOfStoreys",
        "floorRangeStartCode",
        "floorRangeEndCode",
        "yearBuiltCode",
        "yearRefurbishedCode",
        "centralHeating",
        "airConditioning",
        "sprinklers",
        "passengerLift",
        "passengerLiftAllFloors",
        "goodsLift",
        "goodsLiftAllFloors"
      ]
    },
    "factorywarehousesurveyunit": {
