{
  "description": "Attach Challenge Case Evidence Request",
  "type": "object",
  "additionalProperties": false,
  "required": [
    "fileName",
    "mimeType",
    "successRedirect",
    "errorRedirect"
  ],
  "properties": {
    "fileName": {
      "type": "string",
      "minLength": 1,
      "maxLength": 255,
      "description": "The name of the file to be submitted as evidence"
    },
    "mimeType": {
      "type": "string",
      "minLength": 1,
      "description": "The MIME type of the file being submitted"
    },
    "successRedirect": {
      "type": "string",
      "format":  "uri",
      "description": "URL you will be redirected to following a successful file upload"
    },
    "errorRedirect": {
      "type": "string",
      "format":  "uri",
      "description": "URL you will be redirected to, should the file upload fail for any reason"
    }
  }
}

{
  "title": "PreparedUpload",
  "type": "object",
  "additionalProperties": false,
  "required": [
    "uploadRequest",
    "reference"
  ],
  "properties": {
    "reference": {
      "title": "Reference",
      "type": "string"
    },
    "uploadRequest": {
      "title": "UploadFormTemplate",
      "type": "object",
      "additionalProperties": false,
      "required": [
        "fields",
        "href"
      ],
      "properties": {
        "fields": {
          "type": "object",
          "additionalProperties": false,
          "properties": {
            "x-amz-meta-callback-url": {
              "type": "string"
            },
            "x-amz-algorithm": {
              "type": "string"
            },
            "x-amz-signature": {
              "type": "string"
            },
            "key": {
              "type": "string"
            },
            "acl": {
              "type": "string"
            },
            "x-amz-date": {
              "type": "string"
            },
            "x-amz-credential": {
              "type": "string"
            },
            "Content-Type": {
              "type": "string"
            },
            "policy": {
              "type": "string"
            },
            "success_action_redirect": {
              "type": "string",
              "format": "uri"
            },
            "error_action_redirect": {
              "type": "string",
              "format": "uri"
            }
          },
          "items": {
            "type": "string"
          }
        },
        "href": {
          "type": "string",
          "format": "uri",
          "pattern": "^https?://\\w+"
        }
      }
    }
  }
}

{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "description": "My Organisation's Challenge Case Evidence attachment",
  "type": "object",
  "additionalProperties": false,
  "definitions": {},
  "required": [
    "evidence",
    "_links"
  ],
  "properties": {
    "_links": {
      "type": "object",
      "additionalProperties": false,
      "properties": {
        "self": {
          "type": "object",
          "additionalProperties": false,
          "properties": {
            "href": {
              "type": "string",
              "format": "uri",
              "pattern": "^https?://\\w+",
              "description": "URL for this link"
            },
            "template": {
              "type": "boolean",
              "description": "Is the HREF a RFC-6570 compliant URI Template"
            }
          },
          "required": [
            "href",
            "template"
          ]
        }
      }
    },
    "evidence": {
      "type": "object",
      "additionalProperties": false,
      "required": [
        "history",
        "state",
        "data",
        "destination",
        "mimeType",
        "fileName"
      ],
      "properties": {
        "data": {
          "type": "object",
          "additionalProperties": false,
          "description": "The additional data provided with this piece of evidence",
          "required": [
            "challengeSubmissionId"
          ],
          "properties": {
            "challengeSubmissionId": {
              "type": "string",
              "description": "The challenge case submission ID"
            }
          }
        },
        "destination": {
          "type": "string",
          "description": "The destination for upload e.g CHALLENGE_CASE_EVIDENCE"
        },
        "fileName": {
          "type": "string",
          "description": "The provided filename"
        },
        "history": {
          "type": "array",
          "description": "The history of the attachment",
          "items": {
            "type": "object",
            "additionalProperties": false,
            "required": [
              "timeStamp",
              "state"
            ],
            "properties": {
              "state": {
                "type": "string",
                "description": "The state of evidence upload"
              },
              "timeStamp": {
                "type": "string",
                "format": "date-time",
                "description": "The time stamp of change"
              }
            }
          }
        },
        "mimeType": {
          "type": "string",
          "description": "The provided mime-type"
        },
        "state": {
          "type": "string",
          "description": "The current state of evidence upload"
        }
      }
    }
  }
}

