{
  "description": "Create Client Property Link Request",
  "type": "object",
  "additionalProperties": false,
  "required": [
    "clientOrganisationId",
    "uarn",
    "capacity",
    "providedEvidence",
    "evidence",
    "startDate"
  ],
  "properties": {
    "clientOrganisationId": {
      "description": "Unique ID for the client organisation",
      "type": "integer",
      "maximum": 9223372036854775807
    },
    "uarn": {
      "description": "The UARN (Unique Address Reference Number) of the property",
      "type": "integer",
      "maximum": 9223372036854775807
    },
    "capacity": {
      "type": "string",
