          "settledDate": {
            "type": "string",
            "description": "The date this check case was settled"
          },
          "submittedBy": {
            "type": "string",
            "description": "the organisation that submitted check case"
          },
          "uarn": {
            "type": "number",
            "format": "number",
            "description": "the unique address reference of the property"
          }
        }
      }
    }
  }
}

