      "additionalProperties": false,
      "required": [
        "history",
        "state",
        "data",
        "destination",
        "mimeType",
        "fileName"
      ],
      "properties": {
        "data": {
          "type": "object",
          "additionalProperties": false,
          "description": "The additional data provided with this piece of evidence",
          "required": [
            "propertyLinkSubmissionId"
          ],
          "properties": {
            "propertyLinkSubmissionId": {
              "type": "string",
              "description": "The property link submission ID"
            }
          }
        },
        "destination": {
          "type": "string",
          "description": "The destination for upload e.g PROPERTY_LINKING"
        },
        "fileName": {
          "type": "string",
          "description": "The provided filename"
        },
        "history": {
          "type": "array",
          "description": "The history of the attachment",
          "items": {
            "type": "object",
            "additionalProperties": false,
            "required": [
              "timeStamp",
              "state"
            ],
            "properties": {
              "state": {
                "type": "string",
                "description": "The state of evidence upload"
              },
              "timeStamp": {
                "type": "string",
                "format": "date-time",
                "description": "The time stamp of change"
              }
            }
          }
        },
        "mimeType": {
          "type": "string",
          "description": "The provided mime-type"
        },
        "state": {
          "type": "string",
          "description": "The current state of evidence upload"
        }
      }
    }
  }
}

