        "passengerLiftAllFloors": {
          "description": "Passenger lift - all floors",
          "$ref": "#/definitions/booleanDated"
        },
        "goodsLift": {
          "description": "Goods lift",
          "$ref": "#/definitions/booleanDated"
        },
        "goodsLiftAllFloors": {
          "description": "Goods lift - all floors",
          "$ref": "#/definitions/booleanDated"
        }
      },
      "title": "Factory warehouse changes",
      "required": [
        "surveyUnitId",
        "surveyUnitType",
        "surveyLinesList",
        "eavesHeight",
        "numberOfStoreys",
        "floorRangeStartCode",
        "floorRangeEndCode",
        "yearBuiltCode",
        "yearRefurbishedCode",
        "centralHeating",
        "airConditioning",
        "sprinklers",
        "passengerLift",
        "passengerLiftAllFloors",
        "goodsLift",
        "goodsLiftAllFloors"
      ]
    },
    "factorywarehousesurveyunit": {
      "description": "Factory or Warehouse specific information",
      "type": "object",
      "additionalProperties": false,
