{
  "description": "Create Client Property Link Request",
  "type": "object",
  "additionalProperties": false,
  "required": [
    "clientOrganisationId",
    "uarn",
    "capacity",
    "providedEvidence",
    "evidence",
    "startDate"
  ],
  "properties": {
    "clientOrganisationId": {
      "description": "Unique ID for the client organisation",
      "type": "integer",
      "maximum": 9223372036854775807
    },
    "uarn": {
      "description": "The UARN (Unique Address Reference Number) of the property",
      "type": "integer",
      "maximum": 9223372036854775807
    },
    "capacity": {
      "type": "string",
      "enum": [
        "OWNER",
        "OCCUPIER",
        "OWNER_OCCUPIER"
      ],
      "description": "The capacity of the organisation creating the link"
    },
    "providedEvidence": {
      "type": "string",
      "enum": [
        "RATES_BILL",
        "OTHER"
      ],
      "description": "The type of evidence provided in the evidence array"
    },
    "evidence": {
      "type": "array",
      "minItems": 1,
      "maxItems": 2,
      "items": {
        "description": "Evidence attached to support the property link request",
        "type": "object",
        "additionalProperties": false,
        "required": [
          "evidenceType",
          "fileName"
        ],
        "properties": {
          "evidenceType": {
            "type": "string",
            "description": "The type of evidence",
            "enum": [
              "lease",
              "otherUtilityBill",
              "ratesBill",
              "serviceCharge",
              "waterRateDemand",
              "stampDutyLandTaxForm",
              "license",
              "landRegistryTitle",
              "otherSupportingDocument"
            ]
          },
          "fileName": {
            "description": "The file name of the evidence",
            "type": "string",
            "minLength": 1,
            "maxLength": 255
          }
        }
      }
    },
    "startDate": {
      "type": "string",
      "description": "The start date as a ISO Date String"
    },
    "endDate": {
      "type": "string",
      "description": "The end date as a ISO Date String. The End date must be at least one calendar day after the Start date."
    }
  }
}

